                <span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>view</span> <span class="token attr-name">class</span><span class="token attr-value"><span class="token punctuation attr-equals">=</span><span class="token punctuation">&quot;</span>indicator XtxSwiper--indicator<span class="token punctuation">&quot;</span></span><span class="token punctuation">&gt;</span></span>
