    <span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>button</span> <span class="token attr-name">@click</span><span class="token attr-value"><span class="token punctuation attr-equals">=</span><span class="token punctuation">&quot;</span>$emit(&#39;update:pageSize&#39;, pageSize + 4)<span class="token punctuation">&quot;</span></span><span class="token punctuation">&gt;</span></span>
